svg:
>



var xmlns="http://www.w3.org/2000/svg"
count=0
off=0
id=0
var AN=null
var xlinkns = "http://www.w3.org/1999/xlink";
function startup(evt) {
	Root = document.documentElement;
	A=getRect()
	rightedge=A.x
	bottomedge=A.y
	gg = document.getElementById("gg");
	while ((off-1)*xs/2<rightedge) makeNew()
}

function getRect(){
    try{var rect={x: Root.viewport.width,y:Root.viewport.height}}
    catch(e){var rect={x: window.innerWidth,y:window.innerHeight}}
    return rect
}

xs=200;
ys=173.2;
scale=.5
cc=0
function makeNew(){
	cc++
	if (ys*count>bottomedge/scale) {off++; count=0}
	var clone=gg.cloneNode("true")
	var x=(off - (count%2)/2)*xs
	var y=Math.floor(ys*count)
	clone.setAttributeNS(null,"transform","scale("+scale+") translate("+x+" "+y+")")
	var FP=clone.getElementsByTagName("path")
	FP.item(0).setAttributeNS(null,"fill", color())
	FP.item(1).setAttributeNS(null,"fill", grey())
	FP.item(3).setAttributeNS(null,"fill", grey())
	Root.appendChild(clone);
	count++

}
function rotate(evt){
	evt.target.setAttributeNS(null,"fill", color())
}

function color(){
	var r=parseInt(100+Math.random()*155)
	var g=parseInt(100+Math.random()*155)
	var b=parseInt(100+Math.random()*155)
	return "rgb("+r+","+g+","+b+")";
}
function grey(){
	var c=parseInt(100+Math.random()*155)
	var d=parseInt(100+Math.random()*155)
	return "rgb("+d+","+(d)+","+c+")";
}
